INSERT INTO pixelmanga.samples (id, name, synopsis, publication_date, cover) VALUES (19, 'Jigokuraku', 'Gabimaru es un ninja de la villa de la piedra condenado a muerte y sometido a varias torturas que, al igual que el resto de sus camaradas, no puede morir, a pesar de que así lo desea ya que nada le ata a esta vida y no le tiene apego a la vida humana.. ¿o tal vez no sea así? Conoce su travesía junto con todo el elenco involucrado, a una misteriosa Isla, en busca del perdón y redención, en busca del... Elixir de la Vida', '2022-07-09', 'Jigokuraku-cover.jpeg');